mapper.select:
select a.LOG_ID as logId, a.LOG_TYPE as logType, a.LOG_CONTENT as logContent, a.OPERATE_TYPE as operateType, a.USER_ID as userId, a.IP as ip, a.METHOD as method, a.USER_AGENT as userAgent, a.CLIENT_ID as clientId, a.REQUEST_URL as requestUrl, a.REQUEST_PARAM as requestParam, a.REQUEST_TYPE as requestType, a.OPERATE_RESULT as operateResult, a.COST_TIME as costTime
from T_SYS_LOG a where 1=1
AND a.LOG_TYPE = #{entity.logType}
AND a.USER_ID = #{entity.userId}
AND a.IP = #{entity.ip}
order by a.create_time desc
