mapper.select:
select a.USER_ID as userId, a.USER_NAME as userName, a.SEX as sex, a.ROLE_ID as roleId, a.ORG_ID as orgId, a.MOBILE as mobile, a.ID_CARD_NO as idCardNo, a.EMAIL as email, a.STATUS as status, a.SORT_NO as sortNo, a.REMARK as remark, o.org_name as orgName
from T_SYS_USER a join T_SYS_ORG o ON o.org_id=a.org_id where 1=1
AND a.USER_ID = #{entity.userId}
AND a.USER_NAME = #{entity.userName}
AND a.ORG_ID = #{entity.orgId}
{{authFilterSql}} order by a.sort_no

select distinct a.MENU_ID as menuId, a.MENU_NAME as menuName, a.PARENT_MENU_ID as parentMenuId, a.MENU_ICON as menuIcon, a.MENU_URL as menuUrl, a.MENU_PERMISSIONS as menuPermissions, a.COMPONENT as component, a.REDIRECT as redirect, a.HIDDEN as hidden, a.IS_ROUTE as isRoute, a.ROUTE_NAME as routeName, a.IS_CACHE as isCache, a.affix as affix, a.IS_LEAF as isLeaf, a.SORT_NO as sortNo from T_SYS_MENU a left join T_SYS_ROLE_PERMISSION rp ON a.MENU_ID=rp.MENU_OR_FUNC_ID where 1=1
AND rp.ROLE_ID=#{roleId}
order by a.SORT_NO
